// API client
import axios from "axios";
// importing secret URL to server.
import { API } from "../config";

// API to post/send req to send form message to server/ sendgrid
export const sendMsg = () => {
  axios.get().then((response) => {});
};

// API get all blog posts for blog page
export const getAllBlogPosts = () => {
  axios.get("https://btpblog.herokuapp.com/tech-posts").then((response) => {
    const content = response.data;
    console.log(content);
  });
};

// API to get a single blog post
export const getSingleBlogPost = () => {
  axios.get().then((response) => {});
};

// API to get all events from calendar - In progress
export const getAllEvents = () => {
  axios.get().then((response) => {});
};

// API to get all merchandise - In progress
export const getAllMerchItems = () => {
  axios.get().then((response) => {});
};

// API to get single merchandise item - In progress
export const getSingleMerchItem = () => {
  axios.get().then((response) => {});
};
