// import { GlobalStyles } from "./global";

const Layout = ({ children }) => {
  return (
    // <div className={style.layout}>
    //   <NavigationBar />
    //   {children}
    //   <br />
    //   <Footer />
    // </div>

    // return (
    <div>
      <ThemeProvider theme={lightTheme}>
        <NavigationBar />
        <GlobalStyles />
        <button>Toggle Theme</button>
        {children}
        <br />
        <Footer />
      </ThemeProvider>
    </div>
  );
};
export default Layout;
