import axios from "axios";
import { Component } from "react";
import { Media, Card, Accordion } from "react-bootstrap";

class article extends Component {
  state = {
    postId: "",
    singlePost: [],
  };

  static getInitialProps({ query }) {
    return { query };
  }

  componentDidMount() {
    axios
      .get(
        `https://btpblog.herokuapp.com/tech-posts/${this.props.query.postid}`
      )
      .then((res) => {
        if (res.status >= 400) {
          throw new Error("could not return data");
        }
        const data = res.data;
        this.setState({
          singlePost: data,
        });
      });
  }

  render() {
    let article = this.state.singlePost;
    return (
      <div>
        {article.map((content) => {
          return (
            <div>
              <Container>
                <Card key={content.id}>
                  <Card.Img variant="top" src={blog.featuredImage} />
                  <Card.Body>
                    <Card.Title> {content.title} </Card.Title>
                    <Card.Subtitle> authored by ____ </Card.Subtitle>
                    <Accordion defaultActiveKey="0">
                      <Accordion.Toggle
                        as={Card.Header}
                        variant="link"
                        eventKey="1"
                      >
                        {" "}
                        read more{" "}
                      </Accordion.Toggle>
                      <Accordion.Collapse eventKey="1">
                        <Card.Text>{content.content}</Card.Text>
                      </Accordion.Collapse>
                    </Accordion>
                  </Card.Body>
                </Card>
              </Container>
            </div>
          );
        })}
      </div>
    );
    // return <h1>{this.state.singlePost.title}</h1>;
  }
}

export default article;
