import axios from "axios";
import { Component } from "react";

class article extends Component {
  state = {
    postId: "",
    singlePost: [],
  };

  loading = () => {};
  // .get(`https://btpblog.herokuapp.com/tech-posts/5f70d326af0e7b001748d588`)

  componentDidMount = () => {
    const articleId = this.props.match.params.postId;
    console.log(articleId);
    axios
      .get(`https://btpblog.herokuapp.com/tech-posts/${articleId}`)
      .then((response) => {
        const data = response;
        this.setState({
          postId: blogId,
          singlePost: data,
        });
      });
  };

  render() {
    let article = this.state.data;
    return (
      <Media>
        <img
          width={64}
          height={64}
          className="mr-3"
          src=""
          alt="Generic placeholder"
        />
        <Media.Body>
          <h5>{article.title}</h5>
          <p>{article.body}</p>
        </Media.Body>
      </Media>
    );
  }
}

export default article;
