  componentDidMount() {
    axios
      .get(`https://btpblog.herokuapp.com/tech-posts/${query}`)
      .then((res) => {
        if (res.status >= 400) {
          throw new Error("could not return data");
        }
        const data = res.data;
        this.setState({
          singlePost: data,
        });
      });
  }

  render() {
    console.log(this.props.query);
    return <h1>page id= {}</h1>;
  }
}

export default article;
