import { useState, useEffect } from "react";
import { Media, Card, Button } from "react-bootstrap";
import { getAllBlogPosts } from "../../api/apis";
import style from "../../styles/bootstrapUi.module.css";

const AllBlogPosts = () => {
  const [blogData, setBlogData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  // Return all blog posts.
  // When button clicked, use Next routing logic to use post ID to redirect user to single blog page

  return (
    <div>
      <Card style={{ margin: "10px" }} className={style.blogCard}>
        <Media>
          <img
            width={64}
            height={64}
            className="mr-3"
            src=""
            alt="Generic placeholder"
            style={{ margin: "5px" }}
          />
          <Media.Body>
            <h5>Media Heading</h5>
            <p>
              Cras sit amet nibh libero, in gravida nulla. Nulla vel metus
              scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum
              in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac
              nisi vulputate fringilla. Donec lacinia congue felis in faucibus.
            </p>
          </Media.Body>
        </Media>
      </Card>
    </div>
  );
};

export default AllBlogPosts;
