import React, { Component } from 'react'; 
import axios from 'axios'; 

import InfiniteScroll from 'react-infinite-scroll-component';

import BlogItem from './blogItem'; 

export class Blogs extends Component {

    constructor(props) {
        super(props); 
        this.state = {
            blogs: [], 
        }
    }

    componentDidMount(){
        axios.get('https://api.github.com/users/rjsmall90/repos').then((blogs) => {
        const blog = blogs.data
        this.setState({blogs: blog})
    })
    }

    render(){
        return(
            <div>
                <InfiniteScroll
                     dataLength={this.state.blog.length}
                     hasMore={true}
                >
                    <BlogItem blogs={this.state.blog} />
                </InfiniteScroll>
            </div>
        )
    }



}

export default Blogs;
